import React, { Component } from "react";
import "./App.css";
import Todolist from "./todolist/Todolist.js";

const domain = "http://localhost:9000";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = { apiResponse: "", isLoaded: false, error: null, lists: [] };
  }

  // for now assume we are getting the user's username from the props
  // that are being passed in by a Login component (to be created)
  getLists() {
    const url = domain + "/getLists";
    const body = JSON.stringify({
      username: "Jasper" /*this.props.username*/,
    });

    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: body,
    })
      .then((res) => {
        res.json();
      })
      .then(
        (res) => this.setState({ isLoaded: true, lists: res }),
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }

  callAPI() {
    fetch("http://localhost:9000/testAPI")
      .then((res) => res.text())
      .then((res) => this.setState({ apiResponse: res }))
      .catch((err) => err);
  }

  componentDidMount() {
    this.callAPI();
    this.getLists();
  }

  render() {
    return (
      <div className="App">
        <p>{this.state.apiResponse}</p>
        <ul>
          {this.state.lists.map((item, i) => (
            <li key={i}>{item}</li>
          ))}
        </ul>
      </div>
    );
  }
}
