import React from "react";

function formatDate(str) {
  const today = new Date();
  const currYear = today.getFullYear();
  const currDay = today.getDate();
  const currMonth = today.getMonth() + 1;

  const monthAbbr = [
    "Jan",
    "Feb",
    "Mar",
    "Apr",
    "May",
    "Jun",
    "Jul",
    "Aug",
    "Sep",
    "Oct",
    "Nov",
    "Dec",
  ];

  const year = parseInt(str.substring(0, 4));
  const month = parseInt(str.substring(5, 7));
  const day = parseInt(str.substring(8, 10));

  var formatted =
    monthAbbr[month - 1] + " " + day + (currYear === year ? "" : ", " + year);

  return formatted;
}

function ListItem(props) {
  const title = props.title;
  const dueDate = props.dueDate;
  const formattedDate = formatDate(dueDate);
  return (
    <div className="row">
      <div className="col-1">
        <input className="form-check-input" type="checkbox" value="" />
      </div>
      <div className="col-8 text-left item-title">{title}</div>
      <div className="col-3 text-right item-due-date">{formattedDate}</div>
    </div>
  );
}

export default ListItem;
