import React, { Component } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";

export default class CalendarOverlay extends Component {
  constructor(props) {
    super(props);
    this.state = { selectedDate: this.props.currentlySelectedDate };
  }

  render() {
    console.log("in CalendarOverlay.js: " + this.props.currentlySelectedDate);
    var date = null;

    if (this.state.selectedDate !== "")
      date = new Date(this.props.currentlySelectedDate + " 00:00");
    return (
      <div className="calendar-overlay">
        <Calendar
          defaultValue={date === null ? undefined : date}
          onChange={(date) => this.setDate(date)}
        />
        <div className="row mt-4">
          <div className="col-6">
            <button
              type="button"
              className="btn btn-secondary calendar-overlay-button"
              onClick={() => this.props.handleCalendarOverlayClear()}
            >
              Clear
            </button>
          </div>
          <div className="col-6">
            <button
              type="button"
              className="btn btn-primary  calendar-overlay-button"
              onClick={() => this.props.handleCalendarOverlayOK()}
            >
              OK
            </button>
          </div>
        </div>
      </div>
    );
  }

  setDate(date) {
    var formatted = formatDate(date);
    this.props.setAddItemDate(formatted);
    this.setState({ selecteDate: formatted });
  }
}

function formatDate(date) {
  var day = date.getDate();
  if (day < 10) day = "0" + day.toString();

  var month = date.getMonth() + 1;
  if (month < 10) month = "0" + month.toString();

  var year = date.getFullYear().toString();

  var formatted = year + "-" + month + "-" + day;

  return formatted;
}
