import React, { Component } from "react";

export default class Sidebar extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="sidebar-container py-4">
        <div className="profile-icon mb-4 pl-3">
          <span
            style={{
              color: "white",
              backgroundColor: "rgb(21, 127, 251)",
              borderRadius: "50%",
              padding: "10px",
            }}
          >
            {this.props.firstName.toUpperCase()[0] +
              this.props.lastName.toUpperCase()[0]}
          </span>
        </div>
        <div
          className="sidebar-item py-2 pl-5 row"
          style={{ borderTop: "0.25px solid rgb(187, 187, 187, 0.4)" }}
        >
          <i class="fas fa-calendar-day"></i>Today
        </div>
        <div className="sidebar-item py-2 pl-5 row">
          <i class="fas fa-calendar-week"></i>Next 7 Days
        </div>
        <div className="sidebar-item py-2 pl-5 row">
          <i class="fas fa-calendar"></i>All
        </div>
        {this.props.lists.map((list, index) => {
          return <div className="sidebar-item py-2 pl-5 row">{list.name}</div>;
        })}
      </div>
    );
  }
}
