        lists[i].color = color;
        var items = lists[i].items;
        for (let j = 0; j < items.length; j++) {
          items[j].originalList = newName;
          items[j].color = color;
        }
        break;
      }
    }

    this.setState({ lists: lists });

    const url = domain + "/editList";
    const body = JSON.stringify({
      username: this.state.username,
      oldName: oldName,
      newName: newName,
      color: color,
