import React, { Component } from "react";

import "./Todolist.css";

export default class Details extends Component {
  constructor(props) {
    super(props);
    this.inputRef = React.createRef();
    this.state = {
      title: this.props.title,
    };
  }

  handleBlur = (e) => {
    const title = e.target.value;
    if (title !== this.props.title)
      this.props.setItemTitle(
        this.props.listName,
        this.props.selectedItemID,
        title
      );
  };

  handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      this.inputRef.current.blur();
    }
  };

  componentDidUpdate(prevProp) {
    if (prevProp.title === this.props.title) return;
    // it will update input value for other input change
    this.setState({ title: this.props.title });
  }

  render() {
    const title = this.props.selectedItemTitle;
    const dueDate = this.props.selectedItemDueDate;
    const description = this.props.selectedItemDescription;
    const priority = this.props.selectedItemPriority;

    return (
      <div>
        <input
          spellCheck="false"
          className="details-title-input"
          value={this.state.title}
          onKeyDown={(e) => this.handleKeyPress(e)}
          onBlur={(e) => this.handleBlur(e)}
          ref={this.inputRef}
          // onChange={(e) => this.setState({ title: e.target.value })}
        />
      </div>
    );
  }
}
