import React, { Component } from "react";

export default class Sidebar extends Component {
  constructor(props) {
    super(props);
  }

  render() {
    return (
      <div className="sidebar-container">
        <div className="profile-icon">
          <span
            style={{
              color: "white",
              backgroundColor: "rgb(21, 127, 251)",
              borderRadius: "50%",
              padding: "10px",
            }}
          >
            {this.props.firstName.toUpperCase()[0] +
              this.props.lastName.toUpperCase()[0]}
          </span>
        </div>
        <div className="sidebar-item row">Today</div>
        <div className="sidebar-item row">Next 7 Days</div>
        <div className="sidebar-item row">All</div>
        {this.props.lists.map((list, index) => {
          return <div className="sidebar-item row">{list.name}</div>;
        })}
      </div>
    );
  }
}
