import React, { Component } from "react";

export default class AddListOverlay extends Component {
  constructor(props) {
    super(props);
    this.state = { selectedColor: "", listName: "", listNameExists: false };
  }

  selectColor(e) {
    const color = e.currentTarget.dataset.color;
    this.setState({ selectedColor: color });
  }

  handleSubmit() {
    const listName = this.state.listName;
    const color = this.state.selectedColor;

    if (listNameExists(this.props.lists, listName))
      this.setState({ listNameExists: true });
    else {
      // call create list function
    }
  }

  render() {
    const classes =
      "add-list-overlay shadow font-grey p-4" +
      (this.props.displaying ? " add-list-overlay-displayed" : "");
    return (
      <div className={classes}>
        <div className="text-center">
          <h5>Add List</h5>
        </div>
        <label htmlFor="add-list-title-input">Title</label>
        <input
          id="add-list-title-input"
          className="form-control form-control-sm"
          ref={this.props.addListTitleRef}
          onChange={(e) => {
            this.setState({ listName: e.currentTarget.value });
          }}
        />
        <br />
        <label htmlFor="add-list-color-input">Color</label>
        <div className="d-flex justify-content-around">
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#FDC5F5"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#FDC5F5" }}
            data-color="#FDC5F5"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#861388"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#861388" }}
            data-color="#861388"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#5E0B15"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#5E0B15" }}
            data-color="#5E0B15"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#e01e37"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#e01e37" }}
            data-color="#e01e37"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#EF476F"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#EF476F" }}
            data-color="#EF476F"
            onClick={(e) => this.selectColor(e)}
          />
          <i // red
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#FFD166"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#FFD166" }}
            data-color="#FFD166"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#fb5607"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#fb5607" }}
            data-color="#fb5607"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#ffcdb2"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#ffcdb2" }}
            data-color="#ffcdb2"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#8cb369"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#8cb369" }}
            data-color="#8cb369"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#29BF12"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#29BF12" }}
            data-color="#29BF12"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#06EFB1"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#06EFB1" }}
            data-color="#06EFB1"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#0496FF"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#0496FF" }}
            data-color="#0496FF"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#5390d9"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#5390d9" }}
            data-color="#5390d9"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#023e7d"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#023e7d" }}
            data-color="#023e7d"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#5448C8"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#5448C8" }}
            data-color="#5448C8"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#131200"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#131200" }}
            data-color="#131200"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className={
              "fa fa-circle text-center  add-list-overlay-color" +
              (this.state.selectedColor === "#d9d9d9"
                ? " add-list-overlay-color-selected"
                : "")
            }
            style={{ color: "#d9d9d9" }}
            data-color="#d9d9d9"
            onClick={(e) => this.selectColor(e)}
          />
        </div>
      </div>
    );
  }
}

function listNameExists(lists, listName) {
  for (let i = 0; i < lists.length; i++)
    if (lists.name === listName) return true;

  return false;
}
