import React, { Component } from "react";
import "./Login-Register.css";

export default class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      email: "",
      password: "",
      usernameEntered: false,
      nextClicked: false,
    };
  }

  handleEmailInput(e) {
    var entered = e.target.value === "";

    this.setState({
      email: e.target.value,
      usernameEntered: !entered,
    });
  }

  handleEmailEnter(e) {
    if (this.state.usernameEntered && e.keyCode === 13) {
      this.setState({ nextClicked: true });
    }
  }

  handlePasswordInput(e) {
    this.setState({
      password: e.target.value,
    });
  }

  handleSubmit(e) {
    e.preventDefault(); // prevent it from reloading the page

    this.props.verifyLogin("Jasper", "Jasper99!");
    // make a call to the DB in App.js to verify login info
  }
  render() {
    return (
      <div className="Login py-5 px-4">
        <h1>Doozy</h1>
        <br />
        <br />
        <h3 style={{ fontWeight: "600" }}>Log in to your account</h3>
        <br />
        <br />
        <div className="w-100 text-center">Login with Google</div>
        <br />
        <div className="my-3 w-100 text-center font-grey">or</div>
        <br />
        <form
          onSubmit={(e) => this.handleSubmit(e)}
          className="font-grey font-small"
        >
          <div class="form-group">
            <label for="login-email-input" style={{ fontWeight: "500" }}>
              Email Address <i class="ml-1 info-icon fas fa-info-circle"></i>
            </label>
            <input
              type="email"
              class="form-control form-control-sm"
              id="login-email-input"
              aria-describedby="emailHelp"
              onChange={(e) => this.handleEmailInput(e)}
              onKeydown={(e) => this.handleEmailEnter(e)}
            />
            {/* <small id="emailHelp" class="form-text text-muted">
              This is your email address you used when you registered.
            </small> */}
          </div>
          <div class={"form-group" + (this.state.nextClicked ? "" : " d-none")}>
            <label for="login-password-input" style={{ fontWeight: "500" }}>
              Password
            </label>
            <input
              type="password"
              class="form-control form-control-sm"
              id="login-email-password"
              onChange={(e) => this.handlePasswordInput(e)}
            />
          </div>
          <br />
          <br />

          <button
            className="btn btn-primary px-4"
            type="submit"
            disabled={!this.state.usernameEntered}
          >
            {this.state.nextClicked ? "Log In" : "Next"}
          </button>
          <span className="ml-3 font-small">
            Don't have an account?{" "}
            <span
              onClick={() => {
                this.props.switchToRegister();
              }}
              className="text-primary toggle-prompt-text"
              style={{ cursor: "pointer" }}
            >
              Sign Up
            </span>
          </span>
        </form>
      </div>
    );
  }
}
