import React, { Component } from "react";

export default class AddListOverlay extends Component {
  constructor(props) {
    super(props);
    this.state = { selectedColor: "", listname: "" };
  }

  selectColor(e) {
    const color = e.currentTarget.dataset.color;
    console.log(color);
    this.setState({ selectedColor: color });
  }

  render() {
    console.log("in AddListOverlay.js: " + this.props.displaying);
    const classes =
      "add-list-overlay shadow font-grey p-4" +
      (this.props.displaying ? " add-list-overlay-displayed" : "");
    return (
      <div className={classes}>
        <div className="text-center">
          <h5>Add List</h5>
        </div>
        <label htmlFor="add-list-title-input">Title</label>
        <input
          id="add-list-title-input"
          className="form-control form-control-sm"
        />
        <br />
        <label htmlFor="add-list-color-input">Color</label>
        <div className="d-flex justify-content-around">
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#FDC5F5" }}
            data-color="#FDC5F5"
            onClick={(e) => this.selectColor(e)}
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#861388" }}
            data-color="#861388"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#5E0B15" }}
            data-color="#5E0B15"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#e01e37" }}
            data-color="#e01e37"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#EF476F" }}
            data-color="#EF476F"
          />
          <i // red
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#FFD166" }}
            data-color="#FFD166"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#fb5607" }}
            data-color="#fb5607"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#ffcdb2" }}
            data-color="#ffcdb2"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#8cb369" }}
            data-color="#8cb369"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#29BF12" }}
            data-color="#29BF12"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#06EFB1" }}
            data-color="#06EFB1"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#0496FF" }}
            data-color="#0496FF"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#5390d9" }}
            data-color="#5390d9"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#023e7d" }}
            data-color="#023e7d"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#5448C8" }}
            data-color="#5448C8"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#131200" }}
            data-color="#131200"
          />
          <i
            className="fa fa-circle add-list-overlay-color"
            style={{ color: "#d9d9d9" }}
            data-color="#d9d9d9"
          />
        </div>
      </div>
    );
  }
}
