import React, { Component } from "react";

export default class PrioritiesOverlay extends Component {
  state = { selectedPriority: "medium" };
  render() {
    return (
      <div className="overlay priorities-overlay">
        <div
          className="priorities-overlay-item text-left"
          onClick={(e) => this.handlePriorityItemClick}
        >
          High
        </div>
        <div className="priorities-overlay-item text-left">Medium</div>
        <div className="priorities-overlay-item text-left">Low</div>
      </div>
    );
  }

  handlePriorityItemClick(e) {
    const priority = e.target.value.toLowerCase();
    console.log(priority);
    this.props.handlePrioritiesOverlayClick(priority);
  }
}
