import React, { Component } from "react";
import "./App.css";
import Todolist from "./todolist/Todolist.js";

const domain = "http://localhost:9000";

export default class App extends Component {
  constructor(props) {
    super(props);
    this.state = { apiResponse: "", isLoaded: false, error: null, lists: [] };
  }

  // for now assume we are getting the user's username from the props
  // that are being passed in by a Login component (to be created)
  getLists() {
    const url = domain + "/getLists";
    const body = JSON.stringify({
      username: "Jasper" /*this.props.username*/,
    });

    fetch(url, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      body: body,
    })
      .then((res) => res.json())
      .then(
        (res) => {
          // order by due date and then by priority
          var lists = res;
          lists.sort(sortListItems);
          this.setState({ isLoaded: true, lists: lists });
        },
        (error) => {
          this.setState({
            isLoaded: true,
            error,
          });
        }
      );
  }

  // addItem(listName, title, dueDate, description, priority)

  componentDidMount() {
    this.getLists();
  }

  render() {
    return (
      <div className="App">
        {this.state.lists.map((list, i) => (
          <Todolist
            key={i}
            color={list.color}
            name={list.name}
            items={list.items}
          />
        ))}
      </div>
    );
  }
}

function sortListItems(a, b) {
  console.log("is sorting");
  if (a.completed && !b.completed) return -1;
  else if (!a.completed && b.completed) return 1;
  else {
    if (a.dueDate === b.dueDate) {
      const priorities = ["low", "medium", "high"];
      const priorityA = priorities.indexOf(a.priority);
      const priorityB = priorities.indexOf(b.priority);
      return priorityB - priorityA;
    } else {
      const dateA = Date.parse(a.dueDate);
      const dateB = Date.parse(b.dueDate);
      return dateA < dateB;
    }
  }
}
