import React, { Component } from "react";

import "./Todolist.css";

export default class Details extends Component {
  constructor(props) {
    super(props);
    this.inputRef = React.createRef();
    this.state = {
      title: this.props.selectedItemTitle,
      dueDate: this.props.selectedItemDueDate,
      priority: this.props.selectedItemPriority,
      description: this.props.selectedItemDescription,
      completed: this.props.selectedItemCompleted,
    };
  }

  handleBlur = (e) => {
    const title = e.target.value;
    if (title !== this.props.selectedItemTitle)
      this.props.setItemTitle(
        this.props.listName,
        this.props.selectedItemID,
        title
      );
  };

  handleKeyPress = (e) => {
    if (e.keyCode === 13) {
      this.inputRef.current.blur();
    }
  };

  componentDidUpdate(prevProp) {
    if (prevProp.selectedItemTitle === this.props.selectedItemTitle) return;
    // it will update input value for other input change
    this.setState({ title: this.props.selectedItemTitle });
  }

  handleCheck(e) {
    var completed = e.target.checked;

    this.props.setItemCompleted(
      this.props.listName,
      this.state.itemID,
      completed
    );
  }

  render() {
    return (
      <div>
        <div
          id="details-top-row"
          className="row align-items-center mt-4 pb-3 justify-content-around"
        >
          <div className="col-1 details-top-row-check d-flex justify-content-center">
            <input
              className="mx-auto"
              type="checkbox"
              value=""
              checked={this.state.completed}
              onChange={this.handleCheck}
            />
          </div>
          <div className="col-9">date picker </div>
          <div className="col-1">p </div>
        </div>

        <input
          className="mt-2"
          spellCheck="false"
          className="details-title-input"
          value={this.state.title}
          onKeyDown={(e) => this.handleKeyPress(e)}
          onBlur={(e) => this.handleBlur(e)}
          ref={this.inputRef}
          onChange={(e) => this.setState({ title: e.target.value })}
        />
      </div>
    );
  }
}
